shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, serial, timestamp, integer, jsonb } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
});

export const insertUserSchema = createInsertSchema(users).pick({
  username: true,
  password: true,
});

export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;

export interface ATSScore {
  overall: number;
  sections: {
    experience: number;
    skills: number;
    keywords: number;
    formatting: number;
    education: number;
  };
  matchedKeywords: string[];
  missingKeywords: string[];
  recommendations: string[];
  parsedSections: {
    experience?: string;
    skills?: string;
    education?: string;
    summary?: string;
  };
}

export interface CompanyProfile {
  name: string;
  industry: string;
  overview: string;
  history: string;
  financialSituation: string;
  futurePlans: string;
  culture: string;
  interviewStyle: string;
  typicalQuestions: string[];
  values: string[];
  recentNews: string;
  sources: string[];
}

export const interviewSessions = pgTable("interview_sessions", {
  id: serial("id").primaryKey(),
  archetype: text("archetype").notNull(),
  score: integer("score").notNull(),
  transcript: jsonb("transcript").notNull().$type<Array<{ role: "hr" | "user"; text: string }>>(),
  resumeSummary: text("resume_summary"),
  verdict: text("verdict"),
  corporateTitle: text("corporate_title"),
  strengths: jsonb("strengths").$type<string[]>(),
  areasForImprovement: jsonb("areas_for_improvement").$type<string[]>(),
  realAdvice: text("real_advice"),
  interviewTips: jsonb("interview_tips").$type<string[]>(),
  companyName: text("company_name"),
  companyProfile: jsonb("company_profile").$type<CompanyProfile>(),
  atsScore: jsonb("ats_score").$type<ATSScore>(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const insertInterviewSessionSchema = createInsertSchema(interviewSessions).omit({
  id: true,
  createdAt: true,
});

export type InsertInterviewSession = z.infer<typeof insertInterviewSessionSchema>;
export type InterviewSession = typeof interviewSessions.$inferSelect;

client/src/index.css.content:
@import "tailwindcss";
@import "tw-animate-css";

@theme inline {
  --color-background: hsl(0 0% 96%);
  --color-foreground: hsl(0 0% 5%);

  --color-card: hsl(0 0% 100%);
  --color-card-foreground: hsl(0 0% 5%);

  --color-popover: hsl(0 0% 100%);
  --color-popover-foreground: hsl(0 0% 5%);

  --color-primary: hsl(0 0% 5%);
  --color-primary-foreground: hsl(0 0% 98%);

  --color-secondary: hsl(300 100% 50%); /* Magenta */
  --color-secondary-foreground: hsl(0 0% 5%);

  --color-muted: hsl(0 0% 90%);
  --color-muted-foreground: hsl(0 0% 40%);

  --color-accent: hsl(120 100% 40%); /* Acid Green */
  --color-accent-foreground: hsl(0 0% 5%);

  --color-destructive: hsl(0 100% 50%);
  --color-destructive-foreground: hsl(0 0% 98%);

  --color-border: hsl(0 0% 5%);
  --color-input: hsl(0 0% 100%);
  --color-ring: hsl(0 0% 5%);

  --font-sans: 'Space Mono', monospace;
  --font-display: 'Archivo Black', sans-serif;
  --font-weird: 'Syne', sans-serif;

  --radius: 0px;
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply font-sans antialiased bg-background text-foreground overflow-x-hidden;
    background-image: radial-gradient(#000 1px, transparent 1px);
    background-size: 20px 20px;
  }
}

@layer utilities {
  .brutalist-shadow {
    box-shadow: 6px 6px 0px 0px var(--color-foreground);
  }
  .brutalist-shadow-sm {
    box-shadow: 3px 3px 0px 0px var(--color-foreground);
  }
  .brutalist-shadow-lg {
    box-shadow: 12px 12px 0px 0px var(--color-foreground);
  }

  .brutalist-border {
    border: 3px solid var(--color-foreground);
  }

  .text-stroke-1 {
    -webkit-text-stroke: 1px black;
  }
  .text-stroke-2 {
    -webkit-text-stroke: 2px black;
  }
  .text-stroke-white {
    -webkit-text-stroke-color: white;
  }

  .glitch-text {
    position: relative;
  }

  .glitch-text::before,
  .glitch-text::after {
    content: attr(data-text);
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
  }

  .glitch-text::before {
    left: 2px;
    text-shadow: -1px 0 #ff00c1;
    clip: rect(44px, 450px, 56px, 0);
    animation: glitch-anim 5s infinite linear alternate-reverse;
  }

  .glitch-text::after {
    left: -2px;
    text-shadow: -1px 0 #00fff9;
    clip: rect(44px, 450px, 56px, 0);
    animation: glitch-anim2 5s infinite linear alternate-reverse;
  }

  @keyframes glitch-anim {
    0% { clip: rect(38px, 9999px, 86px, 0); }
    20% { clip: rect(6px, 9999px, 16px, 0); }
    40% { clip: rect(82px, 9999px, 12px, 0); }
    60% { clip: rect(2px, 9999px, 86px, 0); }
    80% { clip: rect(55px, 9999px, 34px, 0); }
    100% { clip: rect(12px, 9999px, 8px, 0); }
  }

  @keyframes glitch-anim2 {
    0% { clip: rect(12px, 9999px, 56px, 0); }
    20% { clip: rect(87px, 9999px, 2px, 0); }
    40% { clip: rect(32px, 9999px, 64px, 0); }
    60% { clip: rect(2px, 9999px, 8px, 0); }
    80% { clip: rect(45px, 9999px, 2px, 0); }
    100% { clip: rect(14px, 9999px, 32px, 0); }
  }

  /* Gen-Z Chaos Mode */
  .chaos-rotate {
    animation: chaos-rotate 0.5s ease-in-out;
  }

  @keyframes chaos-rotate {
    0% { transform: rotate(0deg); }
    25% { transform: rotate(-3deg); }
    50% { transform: rotate(3deg); }
    75% { transform: rotate(-1deg); }
    100% { transform: rotate(0deg); }
  }

  .rainbow-border {
    background: linear-gradient(90deg, #ff0000, #ff7f00, #ffff00, #00ff00, #0000ff, #4b0082, #8f00ff);
    background-size: 400% 400%;
    animation: rainbow-shift 3s ease infinite;
  }

  @keyframes rainbow-shift {
    0% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
    100% { background-position: 0% 50%; }
  }

  .neon-glow {
    text-shadow: 0 0 5px #fff, 0 0 10px #fff, 0 0 15px #ff00de, 0 0 20px #ff00de, 0 0 35px #ff00de;
  }

  .neon-glow-green {
    text-shadow: 0 0 5px #fff, 0 0 10px #fff, 0 0 15px #00ff00, 0 0 20px #00ff00, 0 0 35px #00ff00;
  }

  .shake-it {
    animation: shake 0.5s cubic-bezier(.36,.07,.19,.97) both;
  }

  @keyframes shake {
    10%, 90% { transform: translate3d(-1px, 0, 0); }
    20%, 80% { transform: translate3d(2px, 0, 0); }
    30%, 50%, 70% { transform: translate3d(-3px, 0, 0); }
    40%, 60% { transform: translate3d(3px, 0, 0); }
  }

  .float-chaotic {
    animation: float-chaotic 6s ease-in-out infinite;
  }

  @keyframes float-chaotic {
    0%, 100% { transform: translateY(0) rotate(0deg); }
    25% { transform: translateY(-10px) rotate(2deg); }
    50% { transform: translateY(5px) rotate(-1deg); }
    75% { transform: translateY(-5px) rotate(1deg); }
  }

  .spin-slow {
    animation: spin-slow 20s linear infinite;
  }

  @keyframes spin-slow {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
  }

  .vhs-effect {
    position: relative;
  }

  .vhs-effect::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: repeating-linear-gradient(
      0deg,
      rgba(0, 0, 0, 0.1),
      rgba(0, 0, 0, 0.1) 1px,
      transparent 1px,
      transparent 2px
    );
    pointer-events: none;
    z-index: 10;
  }

  .blink-chaos {
    animation: blink-chaos 1s step-end infinite;
  }

  @keyframes blink-chaos {
    0%, 100% { opacity: 1; }
    50% { opacity: 0; }
  }

  .marquee-fast {
    animation: marquee 10s linear infinite;
  }

  @keyframes marquee {
    0% { transform: translateX(0); }
    100% { transform: translateX(-50%); }
  }

  .slay-gradient {
    background: linear-gradient(135deg, #ff00ff 0%, #00ffff 50%, #ff00ff 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .skew-hover:hover {
    transform: skewX(-3deg) scale(1.02);
  }

  .cursed-cursor {
    cursor: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='40' height='48' viewport='0 0 100 100' style='fill:black;font-size:24px;'><text y='50%'>üíÄ</text></svg>") 16 0, auto;
  }

  @keyframes shimmer {
    0% { background-position: 200% 0; }
    100% { background-position: -200% 0; }
  }

  .animate-spin-slow {
    animation: spin 3s linear infinite;
  }

  @keyframes spin {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
  }

  .cyber-grid {
    background-image: 
      linear-gradient(rgba(0, 255, 0, 0.1) 1px, transparent 1px),
      linear-gradient(90deg, rgba(0, 255, 0, 0.1) 1px, transparent 1px);
    background-size: 20px 20px;
  }

  .neon-border {
    box-shadow: 
      0 0 5px var(--color-accent),
      0 0 10px var(--color-accent),
      inset 0 0 5px var(--color-accent);
  }

  .glitch-overlay {
    animation: glitch-overlay 0.3s infinite;
  }

  @keyframes glitch-overlay {
    0%, 100% { opacity: 0; }
    10% { opacity: 0.1; transform: translate(-2px, 1px); }
    20% { opacity: 0; }
    30% { opacity: 0.05; transform: translate(1px, -1px); }
    40%, 100% { opacity: 0; }
  }

  .pulse-glow {
    animation: pulse-glow 2s ease-in-out infinite;
  }

  @keyframes pulse-glow {
    0%, 100% { box-shadow: 0 0 5px var(--color-secondary); }
    50% { box-shadow: 0 0 20px var(--color-secondary), 0 0 30px var(--color-secondary); }
  }

  .typewriter {
    overflow: hidden;
    border-right: 2px solid var(--color-accent);
    animation: typewriter-cursor 0.7s step-end infinite;
  }

  @keyframes typewriter-cursor {
    0%, 100% { border-color: var(--color-accent); }
    50% { border-color: transparent; }
  }

  .scanline {
    position: relative;
  }

  .scanline::after {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: linear-gradient(to right, transparent, var(--color-accent), transparent);
    animation: scanline-move 2s linear infinite;
    opacity: 0.5;
  }

  @keyframes scanline-move {
    0% { top: 0; }
    100% { top: 100%; }
  }

  .text-shadow-glow {
    text-shadow: 
      0 0 10px currentColor,
      0 0 20px currentColor,
      0 0 40px currentColor;
  }

  .hover-lift {
    transition: transform 0.3s ease, box-shadow 0.3s ease;
  }

  .hover-lift:hover {
    transform: translateY(-4px);
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.2);
  }
}

